function lu = crout ( A )[nrow ncol] = size ( A );if ( nrow ~= ncol )   disp ( 'Square coefficient matrix required' );   return;end;A(1,2:nrow) = A(1,2:nrow) / A(1,1);for k = 2:nrow-1    for i = k:nrow	    A(i,k) = A(i,k) - A(i,1:k-1)*A(1:k-1,k);	end	for j = k+1:nrow	    A(k,j) = ( A(k,j) - A(k,1:k-1)*A(1:k-1,j) ) / A(k,k);	endendA(nrow,nrow) = A(nrow,nrow) - A(nrow,1:nrow-1)*A(1:nrow-1,nrow);lu = A;