function lu = doolittle ( A )[nrow ncol] = size ( A );if ( nrow ~= ncol )   disp ( 'Square coefficient matrix required' );   return;end;A(2:nrow,1) = A(2:nrow,1) / A(1,1);for k = 2:nrow-1	for j = k:nrow	    A(k,j) = A(k,j) - A(k,1:k-1)*A(1:k-1,j);	end    for i = k+1:nrow	    A(i,k) = ( A(i,k) - A(i,1:k-1)*A(1:k-1,k) ) / A(k,k);	endendA(nrow,nrow) = A(nrow,nrow) - A(nrow,1:nrow-1)*A(1:nrow-1,nrow);lu = A;