function [lambda, v] = gen_dom ( A, B, x, TOL, Nmax )%POWER_METHOD   approximate the dominant eigenvalue and an associated %               eigenvector for an arbitrary matrix using the power%               method%%     calling sequences:%             [lambda, v] = power_method ( A, x, TOL, Nmax )%             lambda = power_method ( A, x, TOL, Nmax )%             power_method ( A, x, TOL, Nmax )%%     inputs:%             A       square matrix whose dominant eigenvalue is to be %                     approximated%             x       initial approximation to eigenvector corresponding%                     to the dominant eigenvalue%             TOL     absolute error convergence tolerance%                     (convergence is measured in terms of the maximum%                     norm of the difference between successive terms %                     in the eigenvector seqeunce)%             Nmax    maximum number of iterations to be performed%%     outputs:%             lambda  approximation to dominant eigenvalue of A%             v       an eigenvector of A corresponding to the eigenvalue%                     lambda - vector will be normalized to unit length%                     in the maximum norm%%     NOTE:%             if POWER_METHOD is called with no output arguments, the %             iteration number, the current eigenvector approximation, %             the current eigenvalue approximation and an estimate of %             the rate of convergence of the eigenvalue sequence will%             be displayed%%             if the maximum number of iterations is exceeded, a message%             to this effect will be displayed and the most recent%             approximations to the dominant eigenvalue and its corresponding%             eigenvector will be returned in the output values%[r c] = size ( A );[rx rc] = size ( x );if ( rx == 1 ) x = x'; rx = rc; end;if ( r ~= c )   disp ( 'power_method error: matrix must be square' );   return;elseif ( r ~= rx )   disp ( 'power_method error: dimensions of matrix and vector are not compatible' );   return;end;p = min ( find ( abs(x)  == max(abs(x)) ) );x = x / x(p);mu_old = 0;if ( nargout == 0 )   s = sprintf ( '%3d \t %10f ', 0, x(1) );   for j = 2 : rx 	   s = sprintf ( '%s%10f ', s, x(j) );   end;   disp ( s );end;[lu pvt] = LUfactor ( B );for i = 1 : Nmax    xnew = LUsolve ( lu, A * x, pvt );	mu = xnew(p);	p = min ( find ( abs(xnew)  == max(abs(xnew)) ) );    xnew = xnew' / xnew(p);		if ( nargout == 0 )	   s = sprintf ( '%3d \t %10f ', i, xnew(1) );	   for j = 2 : rx 	       s = sprintf ( '%s%10f ', s, xnew(j) );	   end;	   s = sprintf ( '%s \t %10f', s, mu );	   if ( i >= 2 ) s = sprintf ( '%s \t \t %10f', s, abs((mu-mu_old)/(mu_old-mu_older)) ); end;	   disp ( s );	end;		err = max ( abs ( x - xnew ) );	if ( err < TOL )	   if ( nargout >= 1 ) lambda = mu; end;	   if ( nargout >= 2 ) v = xnew; end;	   return;	else    	   x = xnew;	   mu_older = mu_old;	   mu_old = mu;	end;	end;disp ( 'power_method error: Maximum number of iteration exceeded' );if ( nargout >= 1 ) lambda = mu; end;if ( nargout >= 2 ) v = xnew; end;