function csf = cubic_free ( xi, fi )n = length ( xi );m = length ( fi );if ( n ~= m )   disp ( 'number of ordinates and number of function values must be equal' )   returnendfor i = 1 : n-1    hi(i) = xi(i+1) - xi(i);endfor i = 1 : n-2    dd(i) = 2.0 * ( hi(i) + hi(i+1) );	ri(i) = (3.0/hi(i+1))*(fi(i+2)-fi(i+1))-(3.0/hi(i))*(fi(i+1)-fi(i));enddisp ( [dd' ri'] )temp = tridiagonal ( hi(2:n-2), dd, hi(2:n-2), ri );c = zeros ( n,1 );d = c;   b = c;c(2:n-1) = temp;for i = 1 : n-1    d(i) = (c(i+1)-c(i))/(3.0*hi(i));	b(i) = (fi(i+1)-fi(i))/hi(i) - hi(i)*(c(i+1)+2.0*c(i))/3.0;endif ( nargout == 0 )   disp ( [ xi' fi' b c d ] )else   csf = [ xi' fi' b c d ];end