function [w, x, y, rn] = ell_sornd1 ( a, b, N, c, d, M, f, g, alpha, TOL, Nmax, omega )x = linspace ( a, b, N+1 );y = linspace ( c, d, M+1 );h = (b-a)/N;hsq = h*h;if ( nargout > 3 ) rn = zeros ( 1, Nmax ); end;w = zeros ( N+1, M+1 );for i = 1 : N+1    w(i,M+1) = feval ( g, x(i), y(M+1) );end;for j = 2 : M+1    w(1,  j) = feval ( g, x(1), y(j) );	w(N+1,j) = feval ( g, x(N+1), y(j) );end;fmat = zeros ( N+1, M+1 );for i = 1 : N+1    for j = 1 : M+1	    fmat(i,j) = hsq * feval ( f, x(i), y(j) );	end;end;for its = 1 : Nmax    res_norm = 0;		for i = 2:N	    new = ( 1 - omega ) * w(i,1) + ( omega / 4 ) * ( w(i-1,1) + w(i+1,1) + 2*w(i,2) - fmat(i,1) - 2*h*feval ( alpha, x(i) ) );        res = w(i,1) - new;	    w(i,1) = new;		if ( abs ( res ) > res_norm ) res_norm = abs(res); end;	end;		for j = 2 : M	    for i = 2 : N		    new = ( 1 - omega ) * w(i,j) + ( omega / 4 ) * ( w(i-1,j) + w(i+1,j) + w(i,j-1) + w(i,j+1) - fmat(i,j) );            res = w(i,j) - new;			w(i,j) = new;			if ( abs ( res ) > res_norm ) res_norm = abs(res); end;		end;	end;	if ( nargout > 3 ) rn(its) = res_norm; end;		if ( res_norm < TOL ) disp(its); return; end;end; 	   