function [w, x, y] = triangle ( N, TOL, Nmax )x = linspace ( 0, 1, N+1 );y = linspace ( 0, 0.5, N+1 );deltax = 1/N;deltay = 0.5/N;dx2 = deltax * deltax;lambda = dx2 / ( deltay * deltay );w = zeros ( N+1, N+1 );fmat = -1 * dx2 * ones ( N+1, N+1 );for its = 1 : Nmax    res_norm = 0;	for i = 2 : N		new = ( w(i-1,1) + w(i+1,1) + 2 * lambda * w(i,2) - fmat(i,1) ) / ( 2 * ( 1 + lambda ) );	    res = w(i,1) - new;		w(i,1) = new;		if ( abs ( res ) > res_norm ) res_norm = abs(res); end;	end;	for j = 2 : N-1	    for i = j+1 : N		    new = ( w(i-1,j) + w(i+1,j) + lambda * w(i,j-1) + lambda * w(i,j+1) - fmat(i,j) ) / ( 2 * ( 1 + lambda ) );            res = w(i,j) - new;			w(i,j) = new;			if ( abs ( res ) > res_norm ) res_norm = abs(res); end;		end;	end;			if ( res_norm < dx2*TOL ) disp(its); return; end;end; 	   