for i = 1:5,        N = 5*2^i;        w = wave_eqn ( 0, 1, N, 0, 1, N/2, 1/4, 'bc', 'ic' );		x = linspace ( 0, 1, N+1);		exact = 2 * exp(x) * sinh(1/2) + x;        error = w - exact;        maxerror = max ( abs ( error ) );        rmserror = sqrt ( sum ( error .* error ) / N );        if ( i == 1 )           s = sprintf ( '%d \t %d \t %.10f \t \t %.10f', N/2, N, maxerror, rmserror );        else            s = sprintf ( '%d \t %d \t %.10f \t %.6f \t %.10f \t %.6f', N/2, N, maxerror, oldmax/maxerror, rmserror, oldrms/rmserror );        end;        disp ( s );        oldmax = maxerror;        oldrms = rmserror;    end;