function [w, x] = btcs ( a,b, NX, t0, tf, NT, D, bc, f, source, decay )x = linspace ( a, b, NX+1 );% t = linspace ( t0, tf, NT+1 );dx = (b-a) / NX;dt = (tf-t0) / NT;lambda = D * dt / dx^2;wnew = zeros ( NX+1,1 );wold = zeros ( NX+1,1 );for i = 2 : NX    wold(i) = feval ( f, x(i) );end;sub   = -lambda * ones ( 1, NX-2 );super = sub;for j = 2 : NT+1    [ wnew(1), wnew(NX+1) ] = feval ( bc, t0+(j-1)*dt );	[ ua ub ] = feval ( bc, t0+(j-1)*dt );		diag  = ( 1 + 2 * lambda) * ones ( 1, NX-1 ); 	for i = 2 : NX	    sv(i) = feval ( source, x(i), t0+(j-1)*dt );		diag(i-1) = diag(i-1) + dt * feval ( decay, x(i), t0+(j-1)*dt );	end;		rhs = wold(2:NX)' + [ lambda*ua zeros(1,NX-3) lambda*ub ] + dt*sv(2:NX);    wnew(2:NX) = tridiagonal ( sub, diag, super, rhs );	wold = wnew;end;w = wnew;