function [w, r] = cn_polarnd ( a,b, NX, t0, tf, NT, D, bc, f, source )r = linspace ( a, b, NX+1 );dr = (b-a) / NX;dt = (tf-t0) / NT;lambda = D * dt / (2 * dr^2);mu = D * dt / ( 2 * dr );wnew = zeros ( NX+1,1 );wold = zeros ( NX+1,1 );for i = 1 : NX+1    wold(i) = feval ( f, r(i) );end;[ alpha0, p0, q0, r0 ] = feval ( bc, t0 );for i= 1 : NX+1    sold(i) = feval ( source, r(i), t0 );%	dold(i) = feval ( decay, x(i), t0 );end;sub   = [ -( lambda - 0.5 * mu ./ r(2:NX) ), -2*lambda ];super = [ -4*lambda, -( lambda + 0.5 * mu ./ r(2:NX) ) ];for j = 2 : NT+1    [ alpha1, p1, q1, r1 ] = feval ( bc, t0 + (j-1)*dt );		diag  = ( 1 + 2 * lambda) * ones ( 1, NX+1 );	diag(1) = diag(1) + 2 * lambda; 	rhs(1) = ( 1 - 4 * lambda ) * wold(1) + 4 * lambda * wold(2);	snew(1) = feval ( source, r(1), t0 + (j-1)*dt );		for i = 2 : NX	    rhs(i) = ( lambda - 0.5 * mu / r(i) ) * wold(i-1) + ( 1 - 2 * lambda ) * wold(i) + ( lambda + 0.5 * mu / r(i) ) * wold(i+1);		snew(i) = feval ( source, r(i), t0 + (j-1)*dt );%		dold(i) = feval ( decay, x(i), t0 + (j-1)*dt );%		diag(i-1) = diag(i-1) + (dt/2) * dold(i);	end;	diag(NX+1) = diag(NX+1) + ( lambda + 0.5 * mu / r(NX+1) ) * 2 * dr * p1 / q1;	snew(NX+1) = feval ( source, r(NX+1), t0 + (j-1)*dt );	rhs(NX+1) = 2 * lambda * wold(NX) + ( 1 - 2 * lambda - ( lambda + 0.5 * mu / r(NX+1) ) * 2 * dr * p0 / q0 ) * wold(NX+1) + ( lambda + 0.5 * mu / r(NX+1) ) * 2 * dr * ( r1 / q1 + r0 / q0 );	rhs = rhs + (dt/2) * ( sold + snew );	    wnew = tridiagonal ( sub, diag, super, rhs );	wold = wnew;	sold = snew;	alpha0 = alpha1; p0 = p1; q0 = q1; r0 = r1;end;w = wnew;