function [w, r, t] = ftcs_polarnd ( a,b, NX, t0, tf, NT, D, bc, f )r = linspace ( a, b, NX+1 );t = linspace ( t0, tf, NT+1 );dr = (b-a) / NX;dt = (tf-t0) / NT;lambda = D * dt / dr^2;mu = D * dt / dr;diag = 1 - 2 * lambda;wnew = zeros ( NX+1,1 );wold = zeros ( NX+1,1 );for i = 1 : NX+1    wold(i) = feval ( f, r(i) );end;for j = 2 : NT+1    	wnew(1) = ( diag - 2 * lambda ) * wold(1) + 4 * lambda * wold(2);    for i = 2 : NX	    wnew(i) = ( lambda - 0.5 * mu / r(i) ) * wold(i-1) + ( diag ) * wold(i) + ( lambda + 0.5 * mu / r(i) ) * wold(i+1);	end;	wnew(NX+1) = 2 * lambda * wold(NX) + diag * wold(NX+1) + 2 * dr * feval ( bc, t(j-1) ) * ( lambda + 0.5 * mu / r(NX+1) );		wold = wnew;end;w = wnew;