for i = 1:5,        N = 2^(i+1);        [w,r] = cn_polarnd ( 0, 1, N, 0, 1, 500, 1, 'bcnd', 'f', 'source' );        exact = 1 + 0.25*r.^4;        error = w - exact;        maxerror = max ( abs ( error ) );        rmserror = sqrt ( sum ( error .* error ) / N );        if ( i == 1 )           s = sprintf ( '%d \t %.10f \t \t %.10f', N, maxerror, rmserror );        else            s = sprintf ( '%d \t %.10f \t %.6f \t %.10f \t %.6f', N, maxerror, oldmax/maxerror, rmserror, oldrms/rmserror );        end;        disp ( s );        oldmax = maxerror;        oldrms = rmserror;    end;for i = 1:5,        N = 2^(i-1);        [w,r] = cn_polarnd ( 0, 1, 500, 0, 1, N, 1, 'bcnd', 'f', 'source' );        exact = 1 + 0.25*r.^4;        error = w - exact;        maxerror = max ( abs ( error ) );        rmserror = sqrt ( sum ( error .* error ) / 500 );        if ( i == 1 )           s = sprintf ( '%d \t %.10f \t \t %.10f', N, maxerror, rmserror );        else            s = sprintf ( '%d \t %.10f \t %.6f \t %.10f \t %.6f', N, maxerror, oldmax/maxerror, rmserror, oldrms/rmserror );        end;        disp ( s );        oldmax = maxerror;        oldrms = rmserror;    end;